import Layout from "../global/Layout"
import MainSidebar from "../global/MainSidebar"
import SectionHeader from "../global/SectionHeader"
import UserForm from "../user/UserForm"
import Posts from "./Posts"

const ReplyPost =()=>{

    const handleTitleChange = () => {
        if (roles === 'U') {
            return 'Users';
        }
        if (roles === 'A') {
            return 'Admins';
        }
        if (location.state && location.state.role === null) {
            return 'All Users';
        }
    };
    return<>
        <Layout title="Posts">
            <MainSidebar />
            <div className="main-content">

                <section className="section">
                    <SectionHeader title={handleTitleChange()} />
                    {!formVisibility ? (
                        <>
                            <Posts
                                handleFormVisibilty={handleFormVisibilty}
                                users={data && data.result}
                                total={data && data.total}
                                handAddFormToggle={handAddFormToggle}
                                getUserId={getUserId}
                                isRequesting={isRequesting}
                                // UserListing={UserListing}
                                resetSingleUser={resetSingleUser}
                                deleteUser={deleteUser}
                                deletePost={deletePost}
                                sort={sort}
                                posts={posts}
                                setSort={setSort}
                                setPage={setPage}
                                page={page}
                                count={count}
                                getSearchKeyword={getSearchKeyword}
                                changeStatus={changeStatus}
                                getStatus={getStatus}
                                toggleSort={toggleSort}
                            />

                        </>
                    ) : (
                        <>

                            <UserForm
                                handleFormVisibilty={handleFormVisibilty}
                                isAddForm={isAddForm}
                                userId={userId}
                                setReloadToggle={setReloadToggle}
                                reloadToggle={reloadToggle}
                            />
                        </>
                    )}
                </section>
            </div>
        </Layout>
    </>
}
export default ReplyPost
