import { AXIOS_INSTANCE, CATEGORY_ADD, CATEGORY_UPDATE, GET_CATEGORIES, GET_SINGLE_CATEGORY, DELETE_SINGLE_CATEGORY, CATEGORY_API, ALL_CATEGORY_API, GET_CATEGORY_BY_TYPE, CATEGORY_BY_TYPE_API } from '../constants';
