// add the category

export function categoryAdd(postObj, token) {
  return dispatch => {
    dispatch(getRequest(CATEGORY_ADD.CATEGORY_ADD_REQUEST));
    const getUrl = CATEGORY_API;
    const config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
